import React from 'react'
import { Field, Form, Formik } from 'formik';

const AddProduct = () => {
  return (
    <div className='App'>
        <h1>Sign Up</h1>
        <Formik
           initialValues={{
            producttName:'',
            price:'',
            image:'',
           }}
           onSubmit={(values)=>{
            let users=JSON.parse(localStorage.getItem('products'));
            users.push({
                ...values
            })
            localStorage.setItem('products',JSON.stringify(users))
           }}
        >
        <Form>
            <label htmlFor="producttName">Product Name</label>
            <Field className="form form-control" name='productName' placeholder='Nicat'/>

            <label htmlFor="price">Product Price</label>
            <Field className="form form-control" type='number' name='price' placeholder='0.50'/>

            <label htmlFor="image">Product Url</label>
            <Field className="form form-control" type='url' name='image' placeholder=''/>
            <button  type='submit'>SUBMIT</button>
        </Form>
        </Formik>
    </div>
  )
}

export default AddProduct
